AHf8Dv5BdkCIqqJb.command:
const weaponStyle = await game.dc20rpg.tools.getSimplePopup("select", {header: "Select your Natural Weapon Weapon Style", selectOptions: CONFIG.DC20RPG.DROPDOWN_DATA.weaponStyles});

if (["axe", "bow", "fist", "hammer", "pick", "staff", "sword"].includes(weaponStyle)) {
  let statuses = "";
  switch(weaponStyle) {
    case "axe": statuses = '["bleeding"]'; break;
    case "bow": statuses = '["slowed"]'; break;
    case "fist": statuses = '["grappled"]'; break;
    case "hammer": statuses = '["dazed", "heavilyDazed", "petrified"]'; break;
    case "pick": statuses = '["impaired", "heavilyImpaired"]'; break;
    case "staff": statuses = '["hindered"]'; break;
    case "sword": statuses = '["exposed"]'; break;
  }
  item.update({
    ["system.conditional.hasConditional"]: true,
    ["system.conditional.condition"]: `target.hasAnyCondition(${statuses})`,
    ["system.copyEnhancements.copy"]: false
  });
}
if (["chained", "crossbow", "whip", "spear"].includes(weaponStyle)) {
  let description = "";
  switch(weaponStyle) {
    case "chained": description = "<p><b>Chained Style:</b> Your Attacks ignore Shields and 1/2 Cover, and you deal +1 damage against targets that are wielding a Shield or behind 1/2 Cover.</p>"; break;
    case "crossbow": description = "<p><b>Crossbow Style:</b> You deal +1 damage against the last creature you Attacked with the Weapon since the start of your last turn.</p>"; break;
    case "spear": description = "<p><b>Spear Style:</b> You deal +1 damage against a creature if you moved 2 Spaces towards it immediately before making the Attack or if it is on a Mount.</p>"; break;
    case "whip": description = "<p><b>Whip Style:</b> You deal +1 damage when target is farther than 1 Space from you.</p>"; break;
  }
  item.update({
    ["system.conditional.hasConditional"]: false,
    ["system.enhancements.QdpythsgPdfhdqKN.description"]: description,
    ["system.copyEnhancements.copy"]: true
  });
}

