macros.onDemand:
const damageTypes = {
  ...CONFIG.DC20RPG.elementalDamageTypes,
  ...CONFIG.DC20RPG.mysticalDamageTypes
}

// 1. Select Weapon
const weapons = actor.getWeapons();
const weaponId = await game.dc20rpg.tools.getSimplePopup("select", {header: "Select your Bounded Weapon", selectOptions: weapons});
const weapon = actor.items.get(weaponId);
if (weapon) {
  // 2. Select Damage Type
  const selected = await game.dc20rpg.tools.getSimplePopup("select", {header: "Select your Bounded damage type", selectOptions: damageTypes});
  if (selected) {
    const damageFormula = weapon.system.weaponType === "ranged" ? '1' : '2';
    item.update({
      ["system.copyEnhancements.copyFor"]: `_id="${weaponId}"`,
      ["system.enhancements.NTVyGSMufoVokriY.modifications.damageType"]: selected,
      ["system.enhancements.tXOWadltDwewJwss.modifications.formula.type"]: selected,
      ["system.enhancements.tXOWadltDwewJwss.modifications.formula.formula"]: damageFormula,
    });
  }
}

