kQ9aCGh59yNJFlMh.command:
item.doNotSendToChat = true;

// 0. Select Weapon
const weapons = actor.getAllItemsWithType(["weapon"]);
const selectOptions = game.dc20rpg.tools.toSelectOptions(weapons, "id", "name");
const weaponId = await DC20.dialog.SimplePopup.select("Select a Weapon for the attack", selectOptions);
const weapon = actor.items.get(weaponId);
if (!weapon) {
  ui.notifications.warn("Select Weapon for a Spellstrike!");
  return;
}

// 1. Select Spell
const spells = {};
actor.items.forEach(item => {
  if (item.type === "spell" && ["attack", "check"].includes(item.system.actionType)) spells[item.id] = item.name;
});
const spellId = await DC20.dialog.SimplePopup.select("Select Spell for a Spellstrike", spells);
const spell = actor.items.get(spellId);
if (!spell) {
  ui.notifications.warn("Select Spell for a Spellstrike!");
  return;
}

// 2. Configure Spell Strike
const enhancements = {};
const formulas = {};
spell.allEnhancements.forEach((value, key) => enhancements[key] = value);
weapon.allEnhancements.forEach((value, key) => enhancements[key] = value);
if (spell.system.formulas) {
  Object.entries(spell.system.formulas).forEach(([key, value]) => formulas[key] = value);
} 
if (weapon.system.formulas) {
  Object.entries(weapon.system.formulas).forEach(([key, value]) => formulas[key] = value);
}
const weaponRes = weapon.system.costs.resources;
const spellRes = spell.system.costs.resources; 

// Basic Resources
const costs = {
  resources: {
    ap: (weaponRes.ap || 0) + (spellRes.ap || 0) - 1,
    stamina: (weaponRes.stamina || 0) + (spellRes.stamina || 0),
    mana: (weaponRes.mana || 0) + (spellRes.mana || 0),
    health: (weaponRes.health || 0) + (spellRes.health || 0),
    grit: (weaponRes.grit || 0) + (spellRes.grit || 0),
    restPoints: (weaponRes.restPoints || 0) + (spellRes.restPoints || 0)
  }
}

const weaponHasCustom = Object.keys(weaponRes.custom).length > 0;
const spellHasCustom = Object.keys(spellRes.custom).length > 0;
// Custom Resouces
let custom = {};
if (!weaponHasCustom && spellHasCustom) custom = spellRes.custom;
if (weaponHasCustom && !spellHasCustom) custom = weaponRes.custom;
if (weaponHasCustom && spellHasCustom) {
  // Collect custom resources from weapon
  for (const [key, weaponResource] of Object.entries(weaponRes.custom)) {
    custom[key] = {...weaponResource}
    custom[key].value += (spellRes.custom[key]?.value || 0);
  }
  // Collect remaining custom resources from spells
  for (const [key, spellResouce] of Object.entries(spellRes.custom)) {
    if (custom[key] === undefined) custom[key] = {...spellResouce}
  }
}
costs.resources.custom = custom;

const itemData = {
  img: item.img,
  type: "weapon",
  name: `Spellstrike (${spell.name})`,
  system: {
    itemKey: "spellstrikeAttack",
    description: "Temporary Spellstrike item, should be removed after the roll was made.",
    actionType: "attack",
    attackFormula: {
      checkType: "attack",
      rangeType: weapon.system.attackFormula.rangeType
    },
    rollRequests: {...weapon.system.rollRequests, ...spell.system.rollRequests},
    againstEffects: {...weapon.system.againstEffects, ...spell.system.againstEffects},
    costs: costs,
    enhancements: enhancements,
    formulas: formulas,
    range: spell.system.range,
    properties: weapon.system.properties,
  },
  effects: {...spell.effects}
}

const spellStrike = await Item.create(itemData, { parent: actor });
if (spellStrike) await DC20.dialog.RollDialog.open(actor, spellStrike);
await item.update({["flags.dc20rpg.spellStrikeId"]: spellStrike._id});

