const spellStrikeId = item.flags.dc20rpg.spellStrikeId;
const spellStrike = actor.items.get(spellStrikeId);
if (spellStrike) await spellStrike.delete();
await item.update({
  ["flags.dc20rpg.spellStrikeId"]: "",
  ["system.costs.charges.current"]: 0
});
