const flee = effect.flags.dc20rpg?.flee;
const innerDemons = effect.flags.dc20rpg?.innerDemons;

if (flee) {
  if (actor.resources.ap.checkAndSpend(1)) {
    DC20.dialog.SimplePopup.info("Flee - Save Fail", ["You spend 1 AP to run away from the caster."])
  }
}

if (innerDemons) {
  let dmg = {
    value: innerDemons,
    source: "Inner Demons",
    type: "psychic"
  }
  const calculationTarget = {
    system: {damageReduction: actor.system.damageReduction}
  }
  dmg = game.dc20rpg.tools.calculateForTarget(calculationTarget, {clear: {...dmg}, modified: {...dmg}}, {isDamage: true})
  game.dc20rpg.tools.applyDamage(actor, dmg.modified, {fromEvent: true});
}

