const options = actor.getAllItemsWithType(["spell"], [], true);
const spellId = await DC20.dialog.SimplePopup.select("Select Fiendish Magic Spell", options);
if (!spellId) return;

const currentSpellId = item.flags.dc20rpg.currentSpellId;
if (currentSpellId === spellId) return;

const currentSpell = actor.items.get(currentSpellId);
if (currentSpell) currentSpell.update({["system.knownLimit"]: true});
const newSpell = actor.items.get(spellId);
if (newSpell) newSpell.update({["system.knownLimit"]: false});

item.update({
  ["system.copyEnhancements.copyFor"]: `id=["${spellId}"]`,
  ["system.shortInfo"]: `Selected Spell: ${options[spellId]}`,
  ["flags.dc20rpg.currentSpellId"]: spellId
});

