Yalv653kxQPs5FZf.command:
item.doNotSendToChat = true;
const contestantSpellCheck = game.i18n.localize("dc20rpg.dialog.spellDuel.contCheck");
const contestantManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.contMana");
const yourManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.yourMana");
const header = game.i18n.localize("dc20rpg.dialog.spellDuel.header");
const tieInfo = game.i18n.localize("dc20rpg.dialog.spellDuel.tie");

const questions = [
  {type: "input", label: contestantSpellCheck},
  {type: "input", label: contestantManaSpent},
  {type: "input", label: yourManaSpent}
]
const answers = await DC20.dialog.SimplePopup.open("input", {header: header, inputs: questions});
console.log(answers)
if (!answers) return;

const intValues = [];
for (let i = 0; i < answers.length; i++) {
    const value = parseInt(answers[i]);
    if (isNaN(value)) {
        ui.notifications.error(`You must provide correct value for: '${questions[i]}' field`);
        return;
    }
    else {
        intValues.push(value);
    }
}

// Prepare Spell Duel Roll
const contestDC = intValues[0] + intValues[1];
const manaBonus = intValues[2];

const rollFormula = `d20 + @attackMod.value.spell + ${manaBonus}`;

const rollDetails = {
  "against": contestDC,
  "checkKey": "spe",
  "label": `${header} vs ${contestDC}`,
  "roll": rollFormula,
  "type": "spellCheck",
  "description": item.system.description,
  "costs": {ap: 2, mana: manaBonus}
}

const duelRoll = await DC20.dialog.RollDialog.open(actor, rollDetails);

if (duelRoll?.total === contestDC) {
  DC20.dialog.SimplePopup.info(header, [tieInfo])
}
