MWR4SWwQdnR1rWjz.command:
const selectOptions = {
  brace: "Brace",
  advance: "Advance"
}
const selected = await game.dc20rpg.tools.getSimplePopup("select", {header: "Choose Combat Readiness Benefit", selectOptions: selectOptions});

//===============//
//     BRACE     //
//===============//
if (selected === "brace") {
  // Dodge
  const dodgeEffect = game.dc20rpg.effects.getEffectByKey("dodge", item);
  if (dodgeEffect) await game.dc20rpg.effects.createEffectOn(dodgeEffect.toObject(), actor);


  // ADV on next Save
  const brace = game.dc20rpg.effects.getEffectByKey("brace", item);
  if (brace) await game.dc20rpg.effects.createEffectOn(brace.toObject(), actor);
}

//=================//
//     ADVANCE     //
//=================//
if (selected === "advance") { 
  // Move Action
  if (actor.hasOtherMoveOptions) {
    const key = await game.dc20rpg.tools.getSimplePopup("select", {selectOptions: CONFIG.DC20RPG.DROPDOWN_DATA.moveTypes, header: game.i18n.localize("dc20rpg.dialog.movementType.title"), preselect: "ground"})
    if (key) game.dc20rpg.tools.makeMoveAction(actor, {moveType: key});
  }
  else {
    game.dc20rpg.tools.makeMoveAction(actor);
  }

  // ADV on next Physical Check
  const advance = game.dc20rpg.effects.getEffectByKey("advance", item);
  if (advance) await game.dc20rpg.effects.createEffectOn(advance.toObject(), actor);
}

