macros.preItemRoll:
if (actor.hasOtherMoveOptions) {
  const key = await game.dc20rpg.tools.getSimplePopup("select", {selectOptions: CONFIG.DC20RPG.DROPDOWN_DATA.moveTypes, header: game.i18n.localize("dc20rpg.dialog.movementType.title"), preselect: "ground"})
  if (key) game.dc20rpg.tools.makeMoveAction(actor, {moveType: key});
}
else {
  game.dc20rpg.tools.makeMoveAction(actor);
}

const fullTauntEnh = item.system.enhancements.alzLxSdDUbdYlYGb;
if (fullTauntEnh && fullTauntEnh.number > 0) {
  item.system.againstEffect.untilFirstTimeTriggered = false;
  item.system.againstEffect.untilYourNextTurnEnd = true;
}

macros.rollLevelCheck:
const sizeDif = await game.dc20rpg.tools.getSimplePopup("select", {
    header: "What is the size difference between You and the Target", 
    selectOptions: {
      same: "Same Size", 
      oneSize: "Target is one Size bigger",
      bigger: "Target is two or more Sizes bigger",
    }
  });

if (sizeDif === "oneSize") {
  rollLevel.dis++;
  genesis.push({
    type: "dis",
    sourceName: "You",
    label: "Your Target is One Size bigger",
    value: 1
  });
}

if (sizeDif === "bigger") {
  autoFail.value = true
  genesis.push({
    sourceName: "You",
    label: "Your Target is Two or more Sizes bigger",
    autoFail: true
  });
}

macros.preItemRoll:
item.doNotSendToChat = true;
const contestantSpellCheck = game.i18n.localize("dc20rpg.dialog.spellDuel.contCheck");
const contestantManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.contMana");
const yourManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.yourMana");
const header = game.i18n.localize("dc20rpg.dialog.spellDuel.header");
const tieInfo = game.i18n.localize("dc20rpg.dialog.spellDuel.tie");

const answers = await game.dc20rpg.tools.getSimplePopup("input", {header: header, rows: [contestantSpellCheck, contestantManaSpent, yourManaSpent]});
const intValues = [];
for (let i = 0; i < answers.length; i++) {
    const value = parseInt(answers[i]);
    if (isNaN(value)) {
        ui.notifications.error(`You must provide correct value for: '${questions[i]}' field`);
        return;
    }
    else {
        intValues.push(value);
    }
}

// Prepare Spell Duel Roll
const contestDC = intValues[0] + intValues[1];
const manaBonus = intValues[2];

const rollFormula = `d20 + @attackMod.value.spell + ${manaBonus}`;

const rollDetails = {
  "against": contestDC,
  "checkKey": "spe",
  "label": `${header} vs ${contestDC}`,
  "roll": rollFormula,
  "type": "spellCheck",
  "description": item.system.description,
  "costs": [{key: "mana", value: manaBonus}, {key: "ap", value: 2}]
}

const duelRoll = await game.dc20rpg.tools.promptRoll(actor, rollDetails);

if (duelRoll?.total === contestDC) {
  game.dc20rpg.tools.getSimplePopup("info", {header: header, information: [tieInfo]});
}
