Yalv653kxQPs5FZf.command:
item.doNotSendToChat = true;
const contestantSpellCheck = game.i18n.localize("dc20rpg.dialog.spellDuel.contCheck");
const contestantManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.contMana");
const yourManaSpent = game.i18n.localize("dc20rpg.dialog.spellDuel.yourMana");
const header = game.i18n.localize("dc20rpg.dialog.spellDuel.header");
const tieInfo = game.i18n.localize("dc20rpg.dialog.spellDuel.tie");

const questions = [
  {type: "input", label: contestantSpellCheck},
  {type: "input", label: contestantManaSpent},
  {type: "input", label: yourManaSpent}
]
const answers = await DC20.dialog.SimplePopup.open("input", {header: header, inputs: questions});
if (!answers) return;

const intValues = [];
for (let i = 0; i < answers.length; i++) {
    const value = parseInt(answers[i]);
    if (isNaN(value)) {
        ui.notifications.error(`You must provide correct value for: '${questions[i]}' field`);
        return;
    }
    else {
        intValues.push(value);
    }
}

// Prepare Spell Duel Roll
const contestDC = intValues[0] + (2*intValues[1]);
const manaBonus = intValues[2];

const rollFormula = `d20 + @check.spell + ${(manaBonus * 2)}`;

const rollDetails = {
  "against": contestDC,
  "checkKey": "spe",
  "label": `${header} vs ${contestDC}`,
  "customFormula": rollFormula,
  "type": "spellCheck",
  "description": item.system.description,
  "costs": {ap: 2, mana: manaBonus}
}

// Check for Reactive Spell Focus property
const initialRollMenuValue = {adv: 0, dis: 0};
let reactive = false;
for (const item of actor.getAllItemsWithType(["spellFocus"])) {
  if (item.system.statuses.equipped && item.system.properties.reactive.active) reactive = true;
}
if (reactive) initialRollMenuValue.adv = 1;

const duelRoll = await DC20.dialog.RollDialog.open(actor, rollDetails, options={initialRollMenuValue: initialRollMenuValue});

if (duelRoll?.total === contestDC) {
  DC20.dialog.SimplePopup.info(header, [tieInfo])
}

AHf8Dv5BdkCIqqJb.command:
const weaponStyle = await DC20.dialog.SimplePopup.select("Select your Natural Weapon Weapon Style", CONFIG.DC20RPG.DROPDOWN_DATA.weaponStyles);

if (["axe", "bow", "fist", "hammer", "pick", "staff", "sword"].includes(weaponStyle)) {
  let statuses = "";
  switch(weaponStyle) {
    case "axe": statuses = '["bleeding"]'; break;
    case "bow": statuses = '["slowed"]'; break;
    case "fist": statuses = '["grappled"]'; break;
    case "hammer": statuses = '["dazed", "petrified"]'; break;
    case "pick": statuses = '["impaired"]'; break;
    case "staff": statuses = '["hindered"]'; break;
    case "sword": statuses = '["exposed"]'; break;
  }
  item.update({
    ["system.conditionals.Ksr27Rf3uqxNBC4O.condition"]: `target.hasAnyCondition(${statuses})`,
    ["system.copyEnhancements.copy"]: false
  });
}
if (["chained", "crossbow", "whip", "spear"].includes(weaponStyle)) {
  let description = "";
  switch(weaponStyle) {
    case "chained": description = "<p><b>Chained Style:</b> Your Attacks ignore Shields and 1/2 Cover, and you deal +1 damage against targets that are wielding a Shield or behind 1/2 Cover.</p>"; break;
    case "crossbow": description = "<p><b>Crossbow Style:</b> You deal +1 damage against the last creature you Attacked with the Weapon since the start of your last turn.</p>"; break;
    case "spear": description = "<p><b>Spear Style:</b> You deal +1 damage against a creature if you moved 2 Spaces towards it immediately before making the Attack or if it is on a Mount.</p>"; break;
    case "whip": description = "<p><b>Whip Style:</b> You deal +1 damage when target is farther than 1 Space from you.</p>"; break;
  }
  item.update({
    ["system.conditionals.Ksr27Rf3uqxNBC4O.condition"]: "false",
    ["system.enhancements.QdpythsgPdfhdqKN.description"]: description,
    ["system.copyEnhancements.copy"]: true
  });
}

