const sizeDif = await game.dc20rpg.tools.getSimplePopup("select", {
    header: "What is the size difference between You and the Target", 
    selectOptions: {
      same: "Same Size", 
      oneSize: "Target is one Size bigger",
      bigger: "Target is two or more Sizes bigger",
    }
  });

if (sizeDif === "oneSize") {
  rollLevel.dis++;
  genesis.push({
    type: "dis",
    sourceName: "You",
    label: "Your Target is One Size bigger",
    value: 1
  });
}

if (sizeDif === "bigger") {
  autoFail.value = true
  genesis.push({
    sourceName: "You",
    label: "Your Target is Two or more Sizes bigger",
    autoFail: true
  });
}

