if (!on) return;

const questions = [
  {type: "input", label: "Size"},
  {type: "input", label: "Health"},
  {type: "input", label: "Resistance"},
  {type: "input", label: "Your Save DC"}
]
const answers = await DC20.dialog.SimplePopup.open("input", {header: "Configure Force Dome", inputs: questions});
if (!answers) return;

const size = parseInt(answers[0]) || 3;
const health = parseInt(answers[1]) || 3;
const resist = parseInt(answers[2]) || 2;
const saveDC = parseInt(answers[3]) || 8;

await item.update({
  ["system.target.areas.default.distance"]: size/2
})
await actor.update({
  ["system.resources.health.max"]: health,
  ["system.damageReduction.flat"]: resist,
  ["system.defences.precision.normal"]: saveDC,
  ["system.defences.area.normal"]: saveDC
});
await actor.update({
  ["system.resources.health.value"]: health,
});

